/**
 * Multi Fees extension
 *
 * @category   MageWorx
 * @package    MageWorx_MultiFees
 * @author     MageWorx Dev Team
 */

$installer = $this;
$installer->startSetup();
if (!$installer->getConnection()->tableColumnExists($installer->getTable('multifees_fee'), 'is_onetime')) {
    $installer->getConnection()->addColumn(
	$installer->getTable('multifees_fee'),
	'is_onetime',
	"TINYINT(1) NOT NULL DEFAULT '1' AFTER `input_type`"
    );
}
$installer->endSetup();